import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
} from 'chart.js';
import { Pie, Bar } from 'react-chartjs-2';

ChartJS.register(
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
  Title
);

const AssessmentReport = () => {
  const { studentId, classId } = useParams();
  const navigate = useNavigate();
  const [studentData, setStudentData] = useState(null);
  const [classData, setClassData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [studentRes, classRes] = await Promise.all([
          axios.get(`/api/students/${studentId}`, { withCredentials: true }),
          axios.get(`/api/classes/${classId}`, { withCredentials: true })
        ]);
        setStudentData(studentRes.data);
        setClassData(classRes.data);
      } catch (err) {
        setError('Failed to fetch data');
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [studentId, classId]);

  if (loading) return <div className="text-center p-8">Loading...</div>;
  if (error) return <div className="text-center text-red-600 p-8">{error}</div>;
  if (!studentData || !classData) return <div className="text-center p-8">No data found</div>;

  // Calculate average accuracy for the student
  const studentAssessments = studentData.assessments || [];
  const studentAverageAccuracy = studentAssessments.length > 0
    ? studentAssessments.reduce((acc, curr) => acc + curr.accuracy, 0) / studentAssessments.length
    : 0;

  // Calculate class average accuracy
  const allStudentsAssessments = classData.students.flatMap(student => student.assessments || []);
  const classAverageAccuracy = allStudentsAssessments.length > 0
    ? allStudentsAssessments.reduce((acc, curr) => acc + curr.accuracy, 0) / allStudentsAssessments.length
    : 0;

  // Prepare data for pie chart (Assessment Type Distribution)
  const assessmentTypes = studentAssessments.reduce((acc, curr) => {
    acc[curr.type] = (acc[curr.type] || 0) + 1;
    return acc;
  }, {});

  const pieData = {
    labels: Object.keys(assessmentTypes),
    datasets: [{
      data: Object.values(assessmentTypes),
      backgroundColor: [
        'rgba(255, 99, 132, 0.6)',
        'rgba(54, 162, 235, 0.6)',
        'rgba(255, 206, 86, 0.6)',
      ],
      borderColor: [
        'rgba(255, 99, 132, 1)',
        'rgba(54, 162, 235, 1)',
        'rgba(255, 206, 86, 1)',
      ],
      borderWidth: 1,
    }],
  };

  // Prepare data for bar chart (Accuracy Comparison)
  const barData = {
    labels: ['Student Average', 'Class Average'],
    datasets: [{
      label: 'Accuracy (%)',
      data: [studentAverageAccuracy, classAverageAccuracy],
      backgroundColor: [
        'rgba(75, 192, 192, 0.6)',
        'rgba(153, 102, 255, 0.6)',
      ],
      borderColor: [
        'rgba(75, 192, 192, 1)',
        'rgba(153, 102, 255, 1)',
      ],
      borderWidth: 1,
    }],
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-2xl font-bold">Assessment Report</h1>
        <button
          onClick={() => navigate(-1)}
          className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700"
        >
          Back
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        {/* Student Information */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold mb-4">Student Information</h2>
          <div className="space-y-2">
            <p><span className="font-medium">Name:</span> {studentData.name}</p>
            <p><span className="font-medium">Roll No:</span> {studentData.rollNo}</p>
            <p><span className="font-medium">Gender:</span> {studentData.gender}</p>
            <p><span className="font-medium">Age:</span> {studentData.age}</p>
            <p><span className="font-medium">Email:</span> {studentData.email}</p>
          </div>
        </div>

        {/* Performance Summary */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold mb-4">Performance Summary</h2>
          <div className="space-y-4">
            <div>
              <p className="font-medium">Total Assessments: {studentAssessments.length}</p>
              <p className="font-medium">Average Accuracy: {studentAverageAccuracy.toFixed(2)}%</p>
              <p className="font-medium">Class Average: {classAverageAccuracy.toFixed(2)}%</p>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2.5">
              <div
                className="bg-indigo-600 h-2.5 rounded-full"
                style={{ width: `${studentAverageAccuracy}%` }}
              ></div>
            </div>
          </div>
        </div>

        {/* Assessment Type Distribution */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold mb-4">Assessment Type Distribution</h2>
          <div className="h-64">
            <Pie data={pieData} options={{ maintainAspectRatio: false }} />
          </div>
        </div>

        {/* Accuracy Comparison */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold mb-4">Accuracy Comparison</h2>
          <div className="h-64">
            <Bar
              data={barData}
              options={{
                maintainAspectRatio: false,
                scales: {
                  y: {
                    beginAtZero: true,
                    max: 100,
                  },
                },
              }}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default AssessmentReport;
