import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Pie } from 'react-chartjs-2';

ChartJS.register(ArcElement, Tooltip, Legend);

const Dashboard = () => {
  const navigate = useNavigate();
  const [classes, setClasses] = useState([]);
  const [stats, setStats] = useState({
    totalStudents: 0,
    totalAssessments: 0,
    averageAccuracy: 0
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const config = {
          headers: {
            'x-auth-token': localStorage.getItem('token')
          }
        };

        const [classesRes, statsRes] = await Promise.all([
          axios.get('/api/classes', config),
          axios.get('/api/stats', config)
        ]);

        setClasses(classesRes.data);
        setStats(statsRes.data);
      } catch (err) {
        console.error(err);
        if (err.response.status === 401) {
          localStorage.removeItem('token');
          navigate('/login');
        }
      }
    };

    fetchData();
  }, [navigate]);

  const chartData = {
    labels: ['OCR Assessments', 'Speech Assessments'],
    datasets: [
      {
        data: [stats.ocrAssessments || 0, stats.speechAssessments || 0],
        backgroundColor: ['#4F46E5', '#10B981'],
        borderColor: ['#4338CA', '#059669'],
        borderWidth: 1
      }
    ]
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-3">
            {/* Stats Cards */}
            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <dt className="text-sm font-medium text-gray-500 truncate">
                  Total Students
                </dt>
                <dd className="mt-1 text-3xl font-semibold text-gray-900">
                  {stats.totalStudents}
                </dd>
              </div>
            </div>

            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <dt className="text-sm font-medium text-gray-500 truncate">
                  Total Assessments
                </dt>
                <dd className="mt-1 text-3xl font-semibold text-gray-900">
                  {stats.totalAssessments}
                </dd>
              </div>
            </div>

            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <dt className="text-sm font-medium text-gray-500 truncate">
                  Average Accuracy
                </dt>
                <dd className="mt-1 text-3xl font-semibold text-gray-900">
                  {stats.averageAccuracy.toFixed(1)}%
                </dd>
              </div>
            </div>
          </div>

          {/* Chart */}
          <div className="mt-8">
            <div className="bg-white overflow-hidden shadow rounded-lg">
              <div className="px-4 py-5 sm:p-6">
                <h3 className="text-lg leading-6 font-medium text-gray-900">
                  Assessment Distribution
                </h3>
                <div className="mt-4 h-64">
                  <Pie data={chartData} />
                </div>
              </div>
            </div>
          </div>

          {/* Classes List */}
          <div className="mt-8">
            <div className="bg-white shadow overflow-hidden sm:rounded-md">
              <ul className="divide-y divide-gray-200">
                {classes.map(classItem => (
                  <li key={classItem._id}>
                    <a
                      href={`/classes/${classItem._id}`}
                      className="block hover:bg-gray-50"
                    >
                      <div className="px-4 py-4 sm:px-6">
                        <div className="flex items-center justify-between">
                          <p className="text-sm font-medium text-indigo-600 truncate">
                            {classItem.name}
                          </p>
                          <div className="ml-2 flex-shrink-0 flex">
                            <p className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                              {classItem.students.length} Students
                            </p>
                          </div>
                        </div>
                      </div>
                    </a>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
