import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const NUMERACY_CATEGORIES = {
  NUMBER_RECOGNITION: {
    name: 'Number Recognition',
    levels: [
      { name: 'Basic Numbers', description: 'Identify numbers 1-20' },
      { name: 'Advanced Numbers', description: 'Identify numbers 21-100' },
      { name: 'Number Words', description: 'Read numbers in word form' }
    ]
  },
  BASIC_OPERATIONS: {
    name: 'Basic Operations',
    levels: [
      { name: 'Addition', description: 'Single and double digit addition' },
      { name: 'Subtraction', description: 'Single and double digit subtraction' },
      { name: 'Multiplication', description: 'Basic multiplication tables' },
      { name: 'Division', description: 'Simple division problems' }
    ]
  },
  PATTERNS: {
    name: 'Number Patterns',
    levels: [
      { name: 'Skip Counting', description: 'Count by 2s, 5s, and 10s' },
      { name: 'Number Sequences', description: 'Complete number sequences' },
      { name: 'Odd and Even', description: 'Identify odd and even numbers' }
    ]
  },
  PLACE_VALUE: {
    name: 'Place Value',
    levels: [
      { name: 'Ones and Tens', description: 'Understand ones and tens place' },
      { name: 'Hundreds', description: 'Understand hundreds place' },
      { name: 'Comparing Numbers', description: 'Compare numbers using >, <, =' }
    ]
  },
  MONEY: {
    name: 'Money Skills',
    levels: [
      { name: 'Coin Recognition', description: 'Identify coins and their values' },
      { name: 'Counting Money', description: 'Count and add money amounts' },
      { name: 'Making Change', description: 'Calculate change from purchases' }
    ]
  },
  TIME: {
    name: 'Time and Measurement',
    levels: [
      { name: 'Clock Reading', description: 'Read analog and digital clocks' },
      { name: 'Calendar', description: 'Understand calendar concepts' },
      { name: 'Basic Measurement', description: 'Measure length, weight, and capacity' }
    ]
  },
  FRACTIONS: {
    name: 'Fractions',
    levels: [
      { name: 'Basic Fractions', description: 'Identify and compare fractions' },
      { name: 'Fraction Operations', description: 'Add and subtract simple fractions' },
      { name: 'Fraction Problems', description: 'Solve fraction word problems' }
    ]
  },
  GEOMETRY: {
    name: 'Geometry',
    levels: [
      { name: 'Shapes', description: 'Identify basic geometric shapes' },
      { name: 'Properties', description: 'Understand shape properties' },
      { name: 'Area and Perimeter', description: 'Calculate area and perimeter' }
    ]
  },
  DATA: {
    name: 'Data Handling',
    levels: [
      { name: 'Graphs', description: 'Read and interpret simple graphs' },
      { name: 'Statistics', description: 'Calculate mean, median, and mode' },
      { name: 'Probability', description: 'Understand basic probability' }
    ]
  },
  PROBLEM_SOLVING: {
    name: 'Problem Solving',
    levels: [
      { name: 'Word Problems', description: 'Solve mathematical word problems' },
      { name: 'Logical Reasoning', description: 'Solve logical reasoning problems' },
      { name: 'Pattern Recognition', description: 'Identify and extend patterns' }
    ]
  }
};

const NumeracyAssessment = () => {
  const { studentId, classId } = useParams();
  const navigate = useNavigate();
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [currentLevel, setCurrentLevel] = useState(1);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [isCorrect, setIsCorrect] = useState(null);
  const [score, setScore] = useState(0);
  const [progress, setProgress] = useState({});
  const [error, setError] = useState('');
  const [isSaving, setIsSaving] = useState(false);

  const generateQuestion = (category, level) => {
    // This is a placeholder - you would implement specific question generation logic
    // for each category and level
    switch (category) {
      case 'NUMBER_RECOGNITION':
        return generateNumberRecognitionQuestion(level);
      case 'BASIC_OPERATIONS':
        return generateBasicOperationsQuestion(level);
      // Add cases for other categories
      default:
        return { question: 'Sample question', answer: 'Sample answer' };
    }
  };

  const generateNumberRecognitionQuestion = (level) => {
    switch (level) {
      case 1:
        const num = Math.floor(Math.random() * 20) + 1;
        return { question: `What number is this? ${num}`, answer: num.toString() };
      case 2:
        const num2 = Math.floor(Math.random() * 80) + 21;
        return { question: `What number is this? ${num2}`, answer: num2.toString() };
      case 3:
        const numbers = ['one', 'two', 'three', 'four', 'five', 'six', 'seven', 'eight', 'nine', 'ten'];
        const word = numbers[Math.floor(Math.random() * numbers.length)];
        return { question: `Write this number: ${word}`, answer: (numbers.indexOf(word) + 1).toString() };
      default:
        return { question: 'Invalid level', answer: '' };
    }
  };

  const generateBasicOperationsQuestion = (level) => {
    switch (level) {
      case 1: // Addition
        const num1 = Math.floor(Math.random() * 10) + 1;
        const num2 = Math.floor(Math.random() * 10) + 1;
        return { question: `What is ${num1} + ${num2}?`, answer: (num1 + num2).toString() };
      case 2: // Subtraction
        const num3 = Math.floor(Math.random() * 10) + 1;
        const num4 = Math.floor(Math.random() * num3) + 1;
        return { question: `What is ${num3} - ${num4}?`, answer: (num3 - num4).toString() };
      case 3: // Multiplication
        const num5 = Math.floor(Math.random() * 10) + 1;
        const num6 = Math.floor(Math.random() * 10) + 1;
        return { question: `What is ${num5} × ${num6}?`, answer: (num5 * num6).toString() };
      case 4: // Division
        const num7 = Math.floor(Math.random() * 10) + 1;
        const num8 = Math.floor(Math.random() * 10) + 1;
        const product = num7 * num8;
        return { question: `What is ${product} ÷ ${num7}?`, answer: num8.toString() };
      default:
        return { question: 'Invalid level', answer: '' };
    }
  };

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setCurrentLevel(1);
    const newQuestion = generateQuestion(category, 1);
    setQuestion(newQuestion.question);
    setAnswer(newQuestion.answer);
  };

  const handleAnswerSubmit = (userAnswer) => {
    const isAnswerCorrect = userAnswer.trim() === answer.trim();
    setIsCorrect(isAnswerCorrect);
    
    if (isAnswerCorrect) {
      setScore(prev => prev + 1);
      // Move to next level or category
      if (currentLevel < NUMERACY_CATEGORIES[selectedCategory].levels.length) {
        setCurrentLevel(prev => prev + 1);
        const newQuestion = generateQuestion(selectedCategory, currentLevel + 1);
        setQuestion(newQuestion.question);
        setAnswer(newQuestion.answer);
      } else {
        // Category completed
        setProgress(prev => ({
          ...prev,
          [selectedCategory]: {
            completed: true,
            accuracy: (score + 1) / NUMERACY_CATEGORIES[selectedCategory].levels.length * 100
          }
        }));
      }
    }
  };

  const handleSave = async () => {
    try {
      setIsSaving(true);
      setError('');

      const assessmentData = {
        type: 'numeracy',
        category: selectedCategory,
        level: currentLevel,
        score,
        progress,
        date: new Date().toISOString()
      };

      await axios.post(
        `/api/classes/${classId}/students/${studentId}/assessments`,
        assessmentData,
        { withCredentials: true }
      );

      navigate(`/class/${classId}/student/${studentId}/report`);
    } catch (err) {
      setError('Failed to save assessment. Please try again.');
      console.error('Error saving assessment:', err);
    } finally {
      setIsSaving(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-2xl font-bold">Numeracy Assessment</h1>
        <button
          onClick={() => navigate(-1)}
          className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700"
        >
          Back
        </button>
      </div>

      {!selectedCategory ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {Object.entries(NUMERACY_CATEGORIES).map(([key, category]) => (
            <div
              key={key}
              onClick={() => handleCategorySelect(key)}
              className="bg-white rounded-lg shadow-md p-6 cursor-pointer hover:shadow-lg transition-shadow"
            >
              <h2 className="text-xl font-semibold mb-2">{category.name}</h2>
              <p className="text-gray-600">{category.levels[0].description}</p>
            </div>
          ))}
        </div>
      ) : (
        <div className="max-w-2xl mx-auto">
          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="mb-4">
              <h2 className="text-xl font-semibold">
                {NUMERACY_CATEGORIES[selectedCategory].name} - Level {currentLevel}
              </h2>
              <p className="text-gray-600">
                {NUMERACY_CATEGORIES[selectedCategory].levels[currentLevel - 1].description}
              </p>
            </div>

            <div className="mb-6">
              <p className="text-lg mb-4">{question}</p>
              <input
                type="text"
                value={answer}
                onChange={(e) => setAnswer(e.target.value)}
                className="w-full p-2 border rounded-md"
                placeholder="Enter your answer"
              />
            </div>

            <div className="flex justify-between items-center">
              <button
                onClick={() => handleAnswerSubmit(answer)}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
              >
                Submit Answer
              </button>
              <button
                onClick={handleSave}
                disabled={isSaving}
                className="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 disabled:opacity-50"
              >
                {isSaving ? 'Saving...' : 'Save Assessment'}
              </button>
            </div>

            {isCorrect !== null && (
              <div className={`mt-4 p-4 rounded-md ${isCorrect ? 'bg-green-100' : 'bg-red-100'}`}>
                {isCorrect ? 'Correct!' : 'Incorrect. Try again!'}
              </div>
            )}

            {error && (
              <div className="mt-4 p-4 bg-red-100 text-red-700 rounded-md">
                {error}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default NumeracyAssessment;
