import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Webcam from 'react-webcam';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import Tesseract from 'tesseract.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const StudentAssessment = () => {
  const { classId, studentId } = useParams();
  const navigate = useNavigate();
  const [student, setStudent] = useState(null);
  const [assessmentType, setAssessmentType] = useState('ocr');
  const [isRecording, setIsRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [recordedChunks, setRecordedChunks] = useState([]);
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');
  const webcamRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const [transcript, setTranscript] = useState('');
  const [ocrResult, setOcrResult] = useState('');
  const [expectedAnswer, setExpectedAnswer] = useState('');
  const [accuracy, setAccuracy] = useState(0);
  const [isProcessing, setIsProcessing] = useState(false);
  const chunksRef = useRef([]);
  const recognitionRef = useRef(null);

  useEffect(() => {
    const fetchStudentData = async () => {
      try {
        const res = await axios.get(`/api/students/${studentId}`, { withCredentials: true });
        setStudent(res.data);
      } catch (err) {
        console.error(err);
        if (err.response?.status === 401) {
          navigate('/login');
        } else {
          setError('Failed to load student data');
        }
      }
    };

    fetchStudentData();
  }, [studentId, navigate]);

  const handleCapture = async () => {
    const imageSrc = webcamRef.current.getScreenshot();
    try {
      const config = {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      };
      const formData = new FormData();
      formData.append('image', dataURLtoBlob(imageSrc));
      
      const res = await axios.post(
        `/api/assessments/ocr/${studentId}`,
        formData,
        config
      );
      setResult(res.data);
    } catch (err) {
      setError(err.response.data.msg);
    }
  };

  const handleStartRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      mediaRecorderRef.current = recorder;
      setMediaRecorder(recorder);

      recorder.ondataavailable = e => {
        if (e.data.size > 0) {
          setRecordedChunks(prev => [...prev, e.data]);
        }
      };

      recorder.start();
      setIsRecording(true);
    } catch (err) {
      setError('Error accessing microphone');
    }
  };

  const handleStopRecording = async () => {
    mediaRecorderRef.current.stop();
    setIsRecording(false);

    const audioBlob = new Blob(recordedChunks, { type: 'audio/webm' });
    try {
      const config = {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      };
      const formData = new FormData();
      formData.append('audio', audioBlob);

      const res = await axios.post(
        `/api/assessments/speech/${studentId}`,
        formData,
        config
      );
      setResult(res.data);
      setRecordedChunks([]);
    } catch (err) {
      setError(err.response.data.msg);
    }
  };

  const dataURLtoBlob = dataURL => {
    const arr = dataURL.split(',');
    const mime = arr[0].match(/:(.*?);/)[1];
    const bstr = atob(arr[1]);
    let n = bstr.length;
    const u8arr = new Uint8Array(n);
    while (n--) {
      u8arr[n] = bstr.charCodeAt(n);
    }
    return new Blob([u8arr], { type: mime });
  };

  // Calculate accuracy between two strings
  const calculateAccuracy = (str1, str2) => {
    if (!str1 || !str2) return 0;
    
    // Convert to lowercase and remove punctuation
    const cleanStr1 = str1.toLowerCase().replace(/[.,\/#!$%\^&\*;:{}=\-_`~()]/g, '');
    const cleanStr2 = str2.toLowerCase().replace(/[.,\/#!$%\^&\*;:{}=\-_`~()]/g, '');
    
    // Split into words
    const words1 = cleanStr1.split(/\s+/);
    const words2 = cleanStr2.split(/\s+/);
    
    // Count matching words
    let matches = 0;
    words1.forEach(word => {
      if (words2.includes(word)) matches++;
    });
    
    // Calculate accuracy percentage
    const totalWords = Math.max(words1.length, words2.length);
    return totalWords > 0 ? (matches / totalWords) * 100 : 0;
  };

  // Speech Recognition Setup
  const startSpeechRecognition = () => {
    if ('webkitSpeechRecognition' in window) {
      const recognition = new window.webkitSpeechRecognition();
      recognitionRef.current = recognition;
      recognition.continuous = true;
      recognition.interimResults = true;

      recognition.onresult = (event) => {
        const current = event.resultIndex;
        const transcript = event.results[current][0].transcript;
        setTranscript(transcript);
      };

      recognition.onerror = (event) => {
        setError('Speech recognition error: ' + event.error);
      };

      recognition.start();
      setIsRecording(true);
    } else {
      setError('Speech recognition is not supported in your browser');
    }
  };

  const stopSpeechRecognition = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
      setIsRecording(false);
    }
  };

  // OCR Functionality
  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setIsProcessing(true);
    setError('');

    try {
      const result = await Tesseract.recognize(
        file,
        'eng',
        { logger: m => console.log(m) }
      );
      setOcrResult(result.data.text);
    } catch (err) {
      setError('OCR processing failed: ' + err.message);
    } finally {
      setIsProcessing(false);
    }
  };

  // Evaluate Assessment
  const handleEvaluate = () => {
    if (!expectedAnswer) {
      setError('Please provide the expected answer');
      return;
    }

    let studentResponse = '';
    if (transcript) {
      studentResponse = transcript;
    } else if (ocrResult) {
      studentResponse = ocrResult;
    }

    if (!studentResponse) {
      setError('Please provide either speech or OCR input');
      return;
    }

    const newAccuracy = calculateAccuracy(studentResponse, expectedAnswer);
    setAccuracy(newAccuracy);
  };

  // Save Assessment
  const handleSave = async () => {
    if (!expectedAnswer) {
      setError('Please provide the expected answer');
      return;
    }

    if (accuracy === 0) {
      setError('Please evaluate the assessment before saving');
      return;
    }

    try {
      await axios.post(
        `/api/classes/${classId}/students/${studentId}/assessment`,
        {
          transcript,
          ocrResult,
          expectedAnswer,
          accuracy
        },
        { withCredentials: true }
      );
      navigate(`/class/${classId}`);
    } catch (err) {
      setError('Failed to save assessment');
    }
  };

  if (!student) {
    return <div>Loading...</div>;
  }

  const chartData = {
    labels: student.assessments.map(a => new Date(a.date).toLocaleDateString()),
    datasets: [
      {
        label: 'Assessment Accuracy',
        data: student.assessments.map(a => a.accuracy),
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1
      }
    ]
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-4xl mx-auto">
        <div className="flex items-center justify-between mb-8">
          <h1 className="text-3xl font-bold">Student Assessment</h1>
          <button
            onClick={() => navigate(`/class/${classId}`)}
            className="px-4 py-2 text-gray-600 hover:text-gray-800"
          >
            ‚Üê Back to Class
          </button>
        </div>

        {error && (
          <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded mb-4">
            {error}
          </div>
        )}

        {/* Expected Answer Input */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-8">
          <h2 className="text-xl font-semibold mb-4">Expected Answer</h2>
          <textarea
            value={expectedAnswer}
            onChange={(e) => {
              setExpectedAnswer(e.target.value);
              if (transcript) {
                setAccuracy(calculateAccuracy(transcript, e.target.value));
              }
              if (ocrResult) {
                setAccuracy(calculateAccuracy(ocrResult, e.target.value));
              }
            }}
            placeholder="Enter the expected answer..."
            className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 min-h-[100px]"
          />
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {/* Speech to Text Section */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h2 className="text-xl font-semibold mb-4">Speech to Text</h2>
            <div className="space-y-4">
              <div className="flex gap-4">
                <button
                  onClick={isRecording ? stopSpeechRecognition : startSpeechRecognition}
                  className={`px-4 py-2 rounded-md ${
                    isRecording
                      ? 'bg-red-600 hover:bg-red-700'
                      : 'bg-indigo-600 hover:bg-indigo-700'
                  } text-white`}
                >
                  {isRecording ? 'Stop Recording' : 'Start Recording'}
                </button>
              </div>
              <div className="border rounded-md p-4 min-h-[200px]">
                <p className="text-gray-700">{transcript || 'Speech transcript will appear here...'}</p>
              </div>
            </div>
          </div>

          {/* OCR Section */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h2 className="text-xl font-semibold mb-4">OCR (Image to Text)</h2>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Upload Image
                </label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImageUpload}
                  className="block w-full text-sm text-gray-500
                    file:mr-4 file:py-2 file:px-4
                    file:rounded-md file:border-0
                    file:text-sm file:font-semibold
                    file:bg-indigo-50 file:text-indigo-700
                    hover:file:bg-indigo-100"
                />
              </div>
              {isProcessing && (
                <div className="text-center py-4">
                  <p className="text-gray-600">Processing image...</p>
                </div>
              )}
              <div className="border rounded-md p-4 min-h-[200px]">
                <p className="text-gray-700">{ocrResult || 'OCR result will appear here...'}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Evaluate Button */}
        <div className="mt-8 flex justify-center">
          <button
            onClick={handleEvaluate}
            className="px-6 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500"
          >
            Evaluate Assessment
          </button>
        </div>

        {/* Accuracy Display */}
        {accuracy > 0 && (
          <div className="mt-8 bg-white rounded-lg shadow-md p-6">
            <h2 className="text-xl font-semibold mb-4">Accuracy</h2>
            <div className="flex items-center">
              <div className="w-full bg-gray-200 rounded-full h-4">
                <div
                  className="bg-indigo-600 h-4 rounded-full"
                  style={{ width: `${accuracy}%` }}
                ></div>
              </div>
              <span className="ml-4 text-lg font-semibold">{accuracy.toFixed(1)}%</span>
            </div>
          </div>
        )}

        {/* Save Button */}
        <div className="mt-8 flex justify-end">
          <button
            onClick={handleSave}
            className="px-6 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500"
          >
            Save Assessment
          </button>
        </div>
      </div>
    </div>
  );
};

export default StudentAssessment;
