import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Webcam from 'react-webcam';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const StudentAssessment = () => {
  const { studentId } = useParams();
  const navigate = useNavigate();
  const [student, setStudent] = useState(null);
  const [assessmentType, setAssessmentType] = useState('ocr');
  const [isRecording, setIsRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [recordedChunks, setRecordedChunks] = useState([]);
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');
  const webcamRef = useRef(null);
  const mediaRecorderRef = useRef(null);

  useEffect(() => {
    const fetchStudentData = async () => {
      try {
        const config = {
          headers: {
            'x-auth-token': localStorage.getItem('token')
          }
        };
        const res = await axios.get(`/api/students/${studentId}`, config);
        setStudent(res.data);
      } catch (err) {
        console.error(err);
        if (err.response.status === 401) {
          localStorage.removeItem('token');
          navigate('/login');
        }
      }
    };

    fetchStudentData();
  }, [studentId, navigate]);

  const handleCapture = async () => {
    const imageSrc = webcamRef.current.getScreenshot();
    try {
      const config = {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      };
      const formData = new FormData();
      formData.append('image', dataURLtoBlob(imageSrc));
      
      const res = await axios.post(
        `/api/assessments/ocr/${studentId}`,
        formData,
        config
      );
      setResult(res.data);
    } catch (err) {
      setError(err.response.data.msg);
    }
  };

  const handleStartRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      mediaRecorderRef.current = recorder;
      setMediaRecorder(recorder);

      recorder.ondataavailable = e => {
        if (e.data.size > 0) {
          setRecordedChunks(prev => [...prev, e.data]);
        }
      };

      recorder.start();
      setIsRecording(true);
    } catch (err) {
      setError('Error accessing microphone');
    }
  };

  const handleStopRecording = async () => {
    mediaRecorderRef.current.stop();
    setIsRecording(false);

    const audioBlob = new Blob(recordedChunks, { type: 'audio/webm' });
    try {
      const config = {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      };
      const formData = new FormData();
      formData.append('audio', audioBlob);

      const res = await axios.post(
        `/api/assessments/speech/${studentId}`,
        formData,
        config
      );
      setResult(res.data);
      setRecordedChunks([]);
    } catch (err) {
      setError(err.response.data.msg);
    }
  };

  const dataURLtoBlob = dataURL => {
    const arr = dataURL.split(',');
    const mime = arr[0].match(/:(.*?);/)[1];
    const bstr = atob(arr[1]);
    let n = bstr.length;
    const u8arr = new Uint8Array(n);
    while (n--) {
      u8arr[n] = bstr.charCodeAt(n);
    }
    return new Blob([u8arr], { type: mime });
  };

  if (!student) {
    return <div>Loading...</div>;
  }

  const chartData = {
    labels: student.assessments.map(a => new Date(a.date).toLocaleDateString()),
    datasets: [
      {
        label: 'Assessment Accuracy',
        data: student.assessments.map(a => a.accuracy),
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1
      }
    ]
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          {/* Student Header */}
          <div className="bg-white shadow sm:rounded-lg mb-8">
            <div className="px-4 py-5 sm:p-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900">
                {student.name}
              </h3>
              <p className="mt-1 text-sm text-gray-500">
                {student.assessments.length} Assessments Completed
              </p>
            </div>
          </div>

          {/* Assessment Type Selection */}
          <div className="bg-white shadow sm:rounded-lg mb-8">
            <div className="px-4 py-5 sm:p-6">
              <div className="flex space-x-4">
                <button
                  onClick={() => setAssessmentType('ocr')}
                  className={`px-4 py-2 rounded-md ${
                    assessmentType === 'ocr'
                      ? 'bg-indigo-600 text-white'
                      : 'bg-gray-100 text-gray-700'
                  }`}
                >
                  OCR Assessment
                </button>
                <button
                  onClick={() => setAssessmentType('speech')}
                  className={`px-4 py-2 rounded-md ${
                    assessmentType === 'speech'
                      ? 'bg-indigo-600 text-white'
                      : 'bg-gray-100 text-gray-700'
                  }`}
                >
                  Speech Assessment
                </button>
              </div>
            </div>
          </div>

          {/* Assessment Interface */}
          <div className="bg-white shadow sm:rounded-lg mb-8">
            <div className="px-4 py-5 sm:p-6">
              {assessmentType === 'ocr' ? (
                <div>
                  <Webcam
                    ref={webcamRef}
                    screenshotFormat="image/jpeg"
                    className="w-full max-w-lg mx-auto"
                  />
                  <div className="mt-4 flex justify-center">
                    <button
                      onClick={handleCapture}
                      className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700"
                    >
                      Capture Image
                    </button>
                  </div>
                </div>
              ) : (
                <div>
                  <div className="text-center">
                    <button
                      onClick={isRecording ? handleStopRecording : handleStartRecording}
                      className={`inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white ${
                        isRecording
                          ? 'bg-red-600 hover:bg-red-700'
                          : 'bg-indigo-600 hover:bg-indigo-700'
                      }`}
                    >
                      {isRecording ? 'Stop Recording' : 'Start Recording'}
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Results Display */}
          {result && (
            <div className="bg-white shadow sm:rounded-lg mb-8">
              <div className="px-4 py-5 sm:p-6">
                <h3 className="text-lg leading-6 font-medium text-gray-900">
                  Assessment Result
                </h3>
                <div className="mt-4">
                  <p className="text-sm text-gray-500">Text:</p>
                  <p className="mt-1 text-sm text-gray-900">{result.text}</p>
                  <p className="mt-4 text-sm text-gray-500">Accuracy:</p>
                  <p className="mt-1 text-sm text-gray-900">
                    {(result.accuracy * 100).toFixed(1)}%
                  </p>
                </div>
              </div>
            </div>
          )}

          {/* Progress Chart */}
          <div className="bg-white shadow sm:rounded-lg">
            <div className="px-4 py-5 sm:p-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900">
                Progress Chart
              </h3>
              <div className="mt-4 h-64">
                <Line data={chartData} />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StudentAssessment;
